INSERT INTO `schools` VALUES ('891', '330000', '浙江省', '330100000000', '杭州市', '4133010336', '杭州电子科技大学', '浙江省教育厅', '工科', '本科', '0', '0', '0', 'www.hdu.edu.cn,zhaosheng.hdu.edu.cn.', '0571－86915007', '浙江省杭州市下沙高教园区2号大街1158号', '学校创建于 1956 年，1980年成立杭州电子工业学院，2004年更名为杭州电子科技大学。先后隶属于工业部、电子工业部和信息产业部等中央部委，2000 年开始实行部省共建、以省管为主的管理体制，是浙江省重点建设的教学研究型大学。', 'data/school/logo/hangzhoudianzikejidaxue.jpg', null, null, '2021-12-06 21:04:31');
