INSERT INTO `schools` VALUES ('1070', '340000', '安徽省', '340500000000', '马鞍山市', '4134013614', '马鞍山学院', '安徽省教育厅', '工科', '本科', '0', '0', '0', 'http://www.masu.edu.cn/', '招生中心电话：0555-2900997  0555-2900983\r\n人工智能创新学院： 0555-2900988\r\n大阪医工学院： 0555-2900926\r\n腾讯云大数据学院： 0555-2900956\r\n智造工程学院： 0555-2900978\r\n建筑工程学院： 0555-2900939\r\n艺术设计学院： 0555-2900695\r\n经济与管理学院： 0555-2900905\r\n外国语学院： 0555-2900923', '安徽省马鞍山市当涂县黄池路8号', '马鞍山学院位于安徽省马鞍山市，是一所以工为主，工、经、管、文、艺五大学科门类协调发展的应用型大学。设有人工智能创新学院、大阪医工学院、腾讯云大数据学院、智造工程学院、建筑工程学院、艺术设计学院、经济与管理学院、外国语学院、马克思主义学院和基础部等10个院（部）。', 'data/school/logo/maanshanxueyuan.jpg', null, null, '2021-12-06 21:06:07');
