INSERT INTO `schools` VALUES ('1619', '420000', '湖北省', '420100000000', '武汉市', '4142010487', '华中科技大学', '教育部', '综合', '本科/高职(专科)', '1', '0', '1', 'www.hust.edu.cn', '招生办公室电话：027-87542228;87559946；87557014\r\n电子邮件：zs@hust.edu.cn\r\n咨询热线：027-87550120 87550122 87550123 87550125 87550126 87550127 87551551 87551771 87551881 87551991（6月开通）\r\n\r\n我校在各地招生宣传组联系方式\r\nhttp://zsb.hust.edu.cn/Content_1193.aspx', '湖北省武汉市洪山区珞喻路1037号', '华中科技大学是教育部直属的全国重点大学，是目前国内水平一流的综合性大学之一。学校是首批列入国家“211工程”重点建设和国家“985工程”建设高校。', 'data/school/logo/huazhongkejidaxue.jpg', null, null, '2021-12-06 21:12:31');
