INSERT INTO `schools` VALUES ('2145', '500000', '重庆市', '500100000000', '市辖区', '4150010617', '重庆邮电大学', '重庆市教育委员会', '工科', '本科', '0', '0', '0', 'http://www.cqupt.edu.cn,http://zs.cqupt.edu.cn', '023-62468661', '地址：重庆市南岸区南山街道崇文路2号\r\n编码：400065', '重庆邮电大学是国家布点设立并重点建设的几所邮电高校之一，是工业和信息化部与重庆市共建的一所以信息科学技术为特色和优势，在邮电通信行业、信息产业领域，在西部乃至全国具有重要地位和影响的教学研究型大学。办学60余年来，为信息通信行业和地方培养输送了10万余名人才。', 'data/school/logo/chongqingyoudiandaxue.jpg', null, null, '2021-12-08 09:28:32');
